    NSString * typedKeyWord = [map objectForKey:@"typedKeyword"];
    NSString * eLoc = [map objectForKey:@"eLoc"];
    NSNumber * index = [map objectForKey:@"index"];
    NSString * appVersion = [map objectForKey:@"appVersion"];
