- (NSString*)shapeFormatToString:(MBRouteShapeFormat)mode{
    switch(mode) {
        case 0:
            return @"geojson";
        case 1:
            return @"polyline";
        case 2:
            return @"polyline6";
        default:
            [NSException raise:NSGenericException format:@"Unexpected MODE."];
    }
    return nil;
}

