import React from 'react';
import {Image,TouchableOpacity,NativeModules,} from 'react-native';

export interface PlaceWidgetProps {
  location?:[number,number],
  historyCount?:number,
  zoom?:number,
  saveHistory?:boolean,
  pod?:string,
  backgroundColor?:string,
  tokenizeAddress?:boolean,
  toolbarColor?:string,
  hint?:string,
  filter?:string
}

const Components = {

 //map bottom logo
  mapLogo: () => {
    return (
      <Image
        source={require('../assets/mapmyindia_logo.png')}
        style={{height: 50, width: 100, left: 10}}
        resizeMode="contain"
      />
    );
  },

  openPlaceWidget:async (props:PlaceWidgetProps={})=>{
    if(props.tokenizeAddress===undefined){
      props.tokenizeAddress=true
    }
  return NativeModules.MapmyindiaReactNativePlacePicker.openPlaceWidget(props) 
  }
  
};

export default Components;
